<script>
    // validation of inpute feilds start
    $:{
        if(val_1>100000000){
            val_1=100000000
        }
        if(val_2>30){
            val_2=30;
        }
        if(val_3>15){
            val_3=15;
        }
        if(val_4>30){
            val_4=30
        }
        if(val_5>15){
            val_5=15;
        }
    }; 
    // end....................
    let val_1=10000;
    function myfun(){
        if(val_1<10000){
            val_1=10000;
        }
    }
    let val_max_1= 100000000;
    let val_2=1;
    function myfun_val_2(){
        if(val_2<1){
            val_2=1;
        }else if(val_2>30){
           val_2=30;
    }
    } 
    let val_max_2=30;
    let val_3=0.5;
    function myfun_val_3(){
        if(val_3<0.5){
            val_3=0.5;
        }else if(val_3>15){
            val_3=15;

        }
    }
    let val_max_3=15;
    function myfun_val_4(){
        if(val_4<1){
            val_4=1;
        }else if(val_4>30){
            val_4=30;
        }

    }
    let val_4=1;
    let val_max_4=30;
    let val_5=0.5;
    function myfun_val_5(){
        if(val_5<0.5){
            val_5=0.5;
        }else if (val_5>15){
            val_5=15;
        }
    }
    let val_max_5=15;

    let rate;
    let time;
    let nt;
    let emi_cal;
    let div_cal;
    $: {
        if(val_3>0.1){
            rate=val_3/1200;
            time=12*val_2;
            nt=(1+rate)**time;
            div_cal=nt/(nt-1);
            emi_cal=(val_1*rate*div_cal).toFixed(2)
            if (emi_cal<1 || !(isFinite(emi_cal))){
                emi_cal=0.00;
            };
        }else{
            emi_cal=0.00;
        }
    };

    let rate_2;
    let time_2;
    let nt_2;
    let emi_cal_2;
    let div_cal_2;
    $: {
        if(val_5>0.1){
            rate_2=val_5/1200;
            time_2=12*val_4;
            nt_2=(1+rate_2)**time_2;
            div_cal_2=nt_2/(nt_2-1);
            emi_cal_2=(val_1*rate_2*div_cal_2).toFixed(2)
            if(emi_cal_2<1 || !(isFinite(emi_cal_2))){
                emi_cal_2=0.00
            }
        }else{
            emi_cal_2=0.00;
        }
    }    
    let total_saving_cash;
    let saving_emi;

    $: {
        saving_emi=((emi_cal)-(emi_cal_2)).toFixed();
        if(saving_emi<1){
            saving_emi=0.00;
        }
        total_saving_cash=((emi_cal*12*val_2)-(emi_cal_2*12*val_4)).toFixed();
        if(total_saving_cash<0 || isNaN(total_saving_cash)){
            total_saving_cash=0.00;
        }
    }
    


</script>

